C.......................................................
      CALL OMEGAS( 256 , 258 , 28 ,
     &            PPHIGR(1),PLAMGR(1),WORKR,UGRS(1,1),VGRS(1,1),
     2            DGRS(1,1),DEL,RRS2(LAT),VVEL,PGR(1),SL)
