      ZONA(K)=ZONA(K)+A(I,K)
      ZONB(K)=ZONB(K)+A(I+ 128 ,K)
2     CONTINUE
      ZONA(K)=ZONA(K)/ 128
      ZONB(K)=ZONB(K)/ 128
3     CONTINUE
C     PRINT 100,J,ZON
C100  FORMAT(1H ,I3, 28 (1X,F5.2))
      RETURN
      END
