C-DG35                  WORKC(1,1),WORKC(1,5),WORKC(1,9),WORKC(1,13),
C-DG36                  WORKC(1,17),IWORKC,1,1,1)
C-DG3   DO 513 J=1, 31
C-DG3   DO 513 I=1, 256
